import React, { Component } from 'react';
import { Platform, StyleSheet, Text, View, SafeAreaView } from 'react-native';
import { TextInput, ScrollView } from 'react-native-gesture-handler';

const instructions = Platform.select({
  ios: 'Press Cmd+R to reload,\n' + 'Cmd+D or shake for dev menu',
  android: 'Double tap R on your keyboard to reload,\n' + 'Shake or press menu button for dev menu',
});

export default class App extends Component {
  state = {
    inputValue: '',
    bubbles: []
  }

  onChangeText = (inputValue) => {
    this.setState({
      inputValue
    })
  }

  onSubmitEdit = () => {
    this.setState({
      bubbles: this.state.inputValue.split(' ')
    })
  }

  render() {
    const {inputValue, bubbles} = this.state
    return (
      <SafeAreaView style={styles.safeArea}>
        <TextInput
          style={{ paddingLeft: 10, height: 40, borderColor: 'gray', borderWidth: 1 }}
          onChangeText={text => this.onChangeText(text)}
          value={inputValue}
          onSubmitEditing={this.onSubmitEdit}
        />
        <ScrollView style={styles.scrollView}>
          {
            bubbles.map(bubble => <View style={styles.row}>
              <View style={styles.column}></View>
              <View><Text style>{bubble}</Text></View>
              <View></View>
            </View>
            )
          }
        </ScrollView>
      </SafeAreaView>
    );
  }
}

const styles = StyleSheet.create({
  safeArea: {
    margin: 10,
  },
  scrollView: {

  },
  row: {

  },
  column: {
    width: '33%'
  }
});
