import React, { Component } from 'react';
import { Platform, StyleSheet, Text, View, SafeAreaView, Button } from 'react-native';
import { TextInput, ScrollView } from 'react-native-gesture-handler';
import { Stitch, AnonymousCredential, RemoteMongoClient } from 'mongodb-stitch-react-native-sdk';
import { Header } from 'react-native-elements';

export default class App extends Component {
  state = {
    inputValue: '',
    bubbles: []
  }

  componentDidMount(){
    Stitch.initializeDefaultAppClient('wordbubble-app-vbwei').then(client => {
      this.setState({ client });
      console.log(client);

      if(client.auth.isLoggedIn) {
        this.setState({ currentUserId: client.auth.user.id })
      }
    });
  }

  _onPressLogin = () => {
    this.state.client.auth.loginWithCredential(new AnonymousCredential()).then(user => {
        console.log(`Successfully logged in as user ${user.id}`);
        this.setState({ currentUserId: user.id })
    }).catch(err => {
        console.log(`Failed to log in anonymously: ${err}`);
        this.setState({ currentUserId: undefined })
    });
  }

  onChangeText = (inputValue) => {
    this.setState({
      inputValue
    })
  }

  onSubmitEdit = () => {
    const db = this.state.client
      .getServiceClient(RemoteMongoClient.factory, 'wordbubble-stitch')
      .db('wordbubble-db')

    const wordsCollection = db.collection('words')
    let words = this.state.inputValue.split(' ')
    let bubbles = words.map(word => {
      let result = wordsCollection.updateOne({
        word
      }, 
      {
        $set: {
          lastUsed: new Date()
        }
      }, 
      {
        upsert: true
      })
      console.log(word, result);
      return {
        word,
      }
    })
    this.setState({
      bubbles
    })
  }

  render() {
    const {inputValue, bubbles, currentUserId} = this.state
    return (
      <>
      <Header
        leftComponent={{ icon: 'menu', color: '#fff' }}
        centerComponent={{ text: 'WordBubbles', style: { color: '#fff' } }}
        rightComponent={{ icon: 'home', color: '#fff' }}
      />
      <SafeAreaView style={styles.safeArea}>
        {
          currentUserId ? 
          <>
            <TextInput
              style={{ paddingLeft: 10, height: 40, borderColor: 'gray', borderWidth: 1 }}
              onChangeText={text => this.onChangeText(text)}
              value={inputValue}
              onSubmitEditing={this.onSubmitEdit}
              placeholder="Put your words here"
            />
            <ScrollView style={styles.scrollView}>
              {
                bubbles.map(bubble => <View style={styles.row}>
                  <View style={styles.column}></View>
                  <View style={styles.centerColum}>
                <Text style={styles.bubble}>{bubble.word}</Text>
                  </View>
                  <View style={styles.column}></View>
                </View>
                )
              }
            </ScrollView>
          </> : 
          <Button
          onPress={this._onPressLogin}
          title="Login"/>
        }
      </SafeAreaView>
      </>
    );
  }
}

const styles = StyleSheet.create({
  safeArea: {
    margin: 10,
  },
  scrollView: {

  },
  row: {
    flex: 1, 
    flexDirection: 'row',
    marginBottom: 10,
    marginTop: 10,
  },
  column: {
    width: '33%'
  },
  bubble: {
    textAlign: 'center',
    borderWidth: 1,
    padding: 5,
    borderRadius: 5,
    flexWrap: 'wrap'
  },
  centerColum: {
    flex: 1, 
    alignItems: 'center',
    flexWrap: "wrap"
  }
});
